    private int numberOfIterations = 5;

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        redirectSystemStreams();
        System.out.println("Running");

        int numberOfIndividualsInPopulation = 1000;

        Simulator moransim = new MoranSimulator(numberOfIndividualsInPopulation);

        numberOfIterations = Integer.parseInt(jTextField1.getText());
        System.out.println("numberOfIterations->" +numberOfIterations);
        moransim.run(numberOfIterations);

        System.out.println("Done"); 
    }//GEN-LAST:event_jButton1ActionPerformed

